                "must": [
                    {
                        "query_string" : {
                            "query" : "{{ queryString }}"
                            {% if fieldString %},"default_field": "{{ fieldString }}"{% endif %}
                        }
                    }
                    {% if taxonomyString %}
                        ,{
                          "match": {
                            "taxonomies": "taxonomy:AXCbJbxrkDb1UOUe6RBS"
                          }
                        }
                    {% endif %}
                ]
            }
        }
    {% endif %}
}

options.template:
{% set field = app.request.get('field', 'all_*') %}
{% set taxonomy = app.request.get('taxonomy', '*') %}
{% set fields = {
    'all_*': 'All locales',
    'title_fr': 'Title fr',
    'title_nl': 'Title nl',
    'title_de': 'Title de',
    'title_en': 'Title en'
} %}

{% set taxoQuery = {
	'index': 'scratch_live',
	'type': 'taxonomy'
} %}
{% set taxos = taxoQuery|search %}


<div class="row">
    <form method="GET">
        <div class="col-md-3">
            <input name="query" value="{{ app.request.get('query', '')|e('html_attr') }}" class="form-control" placeholder="Search terms..." autofocus>
        </div>
        <div class="col-md-3">
            <select name="field" value="{{ field|e('html_attr') }}" class="form-control">
                {% for key, label in fields %}
                    <option value="{{ key|e('html_attr') }}" {% if field == key %}selected{% endif %}>{{ label }}</option>
                {% endfor %}
            </select>
        </div>
        <div class="col-md-3">
            <select name="taxonomy" class="form-control">
                <option value="">Select a taxonomy...</option>
                {% for taxo in taxos.hits.hits %}
                    <option value="taxonomy:{{ taxo._id }}" {% if ('taxonomy:'~taxo._id) == taxonomy %}selected{% endif %}>{{ taxo._source.title_en }}</option>
                {% endfor %}
            </select>
        </div>
        <div class="col-md-3">
            <button class="btn btn-success">Filter</button>
        </div>
</form>
</div>

<div class="table-responsive">  
    <div class="box-header">
        <h3 class="box-title">Results: {{ result.hits.hits|length }}/{{ result.hits.total }}</h3>
    </div>
    <!-- /.box-header -->
    <div class="box-body">
		<table id="recap" class="table table-bordered table-striped" style="width: 100%">
		    <colgroup>
			   <col span="1" style="width: 25%;">
			   <col span="1" style="width: 25%;">
			   <col span="1" style="width: 25%;">
			   <col span="1" style="width: 25%;">
			</colgroup>
			<tbody>
				<tr>
				    <th>fr</th>
					<th>nl</th>
					<th>en</th>
					<th>de</th>
				</tr>
				{% for hit in result.hits.hits %}
					<tr>
					    <td><h5>
					            
    					        {% if hit._source.show_fr is defined and hit._source.show_fr %}
    					            {{ (hit._type~':'~hit._id)|data_link|raw }}
    					        {% else %}
    					            {% if hit._source.title_fr is defined %}{{ hit._source.title_fr }}{% endif %}
    					        {% endif %}
					         </h5>
					    </td>
					    <td><h5>
    					        {% if hit._source.show_nl is defined and hit._source.show_nl %}
    					            <b>{% if hit._source.title_nl is defined %}{{ hit._source.title_nl }}{% endif %}</b>
    					        {% else %}
    					            {% if hit._source.title_nl is defined %}{{ hit._source.title_nl }}{% endif %}
    					        {% endif %}
					         </h5>
					    </td>					    
					    <td><h5>
    					        {% if hit._source.show_en is defined and hit._source.show_en %}
    					            <b>{% if hit._source.title_en is defined %}{{ hit._source.title_en }}{% endif %}</b>
    					        {% else %}
    					            {% if hit._source.title_en is defined %}{{ hit._source.title_en }}{% endif %}
    					        {% endif %}
					         </h5>
					    </td>					    
					    <td><h5>
    					        {% if hit._source.show_de is defined and hit._source.show_de %}
    					            <b>{% if hit._source.title_de is defined %}{{ hit._source.title_de }}{% endif %}</b>
    					        {% else %}
    					            {% if hit._source.title_de is defined %}{{ hit._source.title_de }}{% endif %}
    					        {% endif %}
					         </h5>
					    </td>
					</tr>
				{% endfor %}	
			</tbody>
		</table>
    </div>
</div>
