{% set config = 'config'|i18n('en')|json_decode %}
{% set sourceTargets = source.target_group|default([]) %}


{% if source.redirect_to|default(false) %}
    <div class="alert alert-info" role="alert">
       This news item will redirect to {{ source.redirect_to|data_link }}
    </div> 
{% else %}
    <div class="table-responsive">
        <table class="table">
            <caption>List of targets</caption>
            <thead>
                <tr>
                    <th scope="label"></th>
                    {% for targetGroupLabel in config.target_groups|keys %}
                        <th scope="label" class="text-center">{{ targetGroupLabel }}</th>
                    {% endfor %}
                </tr>
            </thead>
            {% for lang, locale in config.languages %}
                <tr>
                    <th>{{ lang }}</th>
                    {% for target_group in config.target_groups %}
                         {% set baseUrl = [('baseUrl.'~environment.name)|i18n(locale), "targetGroup.#{target_group}"|i18n(locale), 'news'|i18n(locale)]|join('/')   %}
                    
                        {% if (target_group in sourceTargets or sourceTargets is empty) and  attribute(source, "show_#{locale}")|default(false) == true %}
                            <td class="text-center">
                                <a target="_blank" href="{{ [baseUrl, source.search_dates|first|date('Y/m/d'), attribute(source, "slug_#{locale}")|default('')]|join('/') }}" class="btn btn-primary btn-sm">
                                    <i class="fa fa-eye" aria-hidden="true"></i>
                                    show
                                </a>
                            </td>
                        {% else %}
                            <td class="text-center"><span><i class="fa fa-times text-danger" aria-hidden="true"></i></span></td>
                        {% endif %}
                    {% endfor %}
                </tr>
            {% endfor %}
        </table>
    </div
{% endif %}

options.body:
{% spaceless %}
    {% set must = [] %}
    {% set body = {
        "sort": [
            {
              "search_date": {
                "order": "desc"
              }
            }
        ] 
    } %}
    
    {% if app.request.query.get('q', false) %}
        {% set must = must|merge({ 
            "query_string": {
                "query": app.request.query.get('q')|default('*'),
                "default_field": app.request.query.get('fields')|default('_all')
            } 
        }) %}
    {% endif %}
    {% if app.request.query.get('search_type', false) %}
        {% set must = must|merge([{
            'term': {"search_type": app.request.query.get('search_type') }    
        }]) %}
    {% endif %}
    {% if app.request.query.get('search_category', false) %}
        {% set must = must|merge([{
            'term': {"search_category": app.request.query.get('search_category') }    
        }]) %}
    {% endif %}
    {% if app.request.query.get('target', false) %}
        {% set must = must|merge([{
            'term': {"target_group": app.request.query.get('target') }    
        }]) %}
    {% endif %}
    
    {% if must %}{% set body = body|merge({'query': { "bool": { "must": must } } } ) %}{% endif %}
{% endspaceless %}
{{- body|json_encode|raw -}}

options.template:
{% set tableLength = 100 %}
{% set environments = ['preview', 'live'] %}
{% set publishViewId = 60 %}
{% set config = 'config'|i18n('en')|json_decode %}

{% set published = [] %}
{% for env in environments %}
    {% set envHits = {
        "index": ("#{config.instance_id}#{env}"),
        "size": 3000,
        "_source": ['_sha1'],
        "body": {"query": {"term": { "_contenttype": { "value": "#{contentType.name}" } } } }
    }|search.hits.hits %}
    {% set envIdSha1 = [] %}
    {% for hit in envHits %}
        {% set envIdSha1 = envIdSha1|merge({ (hit._id): (hit._source._sha1) }) %}
    {% endfor %}
    {% set published = published|merge({ (env): (envIdSha1) }) %}
{% endfor %}

<form method="GET" class="form">
    <div class="well" style="padding: 15px;">
        <div class="row" style="padding-bottom: 10px;">
            <div class="col-md-4">
                <select name="target" class="form-control input-sm">
                    <option value="">All targets</option>
                    {% for tLabel, tValue in config.target_groups %}
                        <option value="{{ tValue }}" {% if app.request.query.get('target')|default(false) == tValue %}selected{% endif %}>Only {{ tLabel }}</option>
                    {% endfor %}
                </select>
            </div>
            <div class="col-md-4">
                <select name="search_category" class="form-control input-sm">
                    <option value="">All categories</option>
                    {% set searchCategories = {
                        "index": ("#{config.instance_id}#{contentType.environment.name}"),
                        "body": { 
                            "size": 0,  
                            "query": { "term": {"_contenttype": ("#{contentType.name}")} },
                            "aggs": { "types": { "terms": { "field": "search_category", "size": 50 } } } 
                        }
                    }|search.aggregations.types.buckets|default([]) %}
                    {% for sC in searchCategories %}
                         <option value="{{ sC.key }}" {% if app.request.query.get('search_category')|default(false) == sC.key %}selected{% endif %}>{{ sC.key|data.title_en|default(sC.key) }}</option>
                    {% endfor %}
                </select>
            </div>
        </div>
        <div class="row">
            <div class="col-md-4">
                <select class="form-control input-sm" name="fields">
                    <option value="_all" {% if app.request.query.get('fields')|default(false) == "_all" %}selected{% endif %}>All fields</option>
                    {% for label, locale in config.languages %}
                        <option value="{{ "all_#{locale}" }}" {% if app.request.query.get('fields')|default(false) == "all_#{locale}" %}selected{% endif %}>{{ "Fields in #{label}" }}</option>
                    {% endfor %}
                </select>
            </div>
            <div class="col-md-4">
                <input type="text" class="form-control input-sm" placeholder="Search text (abc*)" name="q" value="{{ app.request.query.get('q')|default('') }}">
            </div>
            <div class="col-md-4">
                <div>
                    <div class="btn-group">
                        <button type="submit" class="btn btn-sm btn-primary">Search</button>
                        <a href="{{ path('data.customindexview', { viewId: view.id}) }}" class="btn  btn-sm btn-primary">Reset</a>
                    </div>
                    {% if is_granted(contentType.createRole) %}
                        <a href="{{ path('data.add', {contentType: contentType.id}) }}" class="btn btn-sm btn-success pull-right"><i class="fa fa-plus"></i>&nbsp;Add </a>        
                    {% endif%}
                </div>
            </div>
       </div>
    </div>
</form>

<div class="table-responsive">
    <table id="overview" class="table table-bordered table-striped" data-page-length="{{ tableLength }}" >
        <thead>
            <tr>
                {% if is_granted(contentType.editRole) %}<th style="width: 20px;" data-orderable="false"></th>{% endif %}
                <th>label</th>
                <th class="text-center" style="width: 100px;">date</th>
                <th class="text-center" style="width: 100px;">languages</th>
                <th class="text-center" style="width: 100px;">published</th>
                <th class="text-center" style="width: 20px;">info</th>
                {% if is_granted('ROLE_COPY_PASTE') or is_granted('ROLE_ADMIN') %}<th data-orderable="false" style="width: 60px;"></th>{% endif %}
            </tr>
        </thead>
        <tbody>
            {% for hit in result.hits.hits %}
                {% set info %}
                   {{ hit._source._finalization_datetime|date('d-m-Y H:i') }}
                   <br />
                   {{ hit._source._finalized_by is defined ? hit._source._finalized_by : '' }}
                {% endset %}
                <tr>
                    {% if is_granted(contentType.editRole) %}
                        <td><a class="btn btn-xs btn-primary" href="{{ path('revision.new-draft', {'type': contentType.name, 'ouuid': hit._id }) }}"><i class="fa fa-pencil" /></a></td>
                    {% endif %}
                    <td><a href="{{ path('ems_content_revisions_view', {'type': contentType.name, 'ouuid': hit._id }) }}" >{{ hit._source.label }}</a></td>
                    <td class="text-center">{{ hit._source.search_dates|first|date('d-m-Y') }}</td>
                    <td class="text-center" data-order="{{ config.languages|filter(v => attribute(hit._source, "show_#{v}") == true)|join(' ') }}">
                        {% for lang, locale in config.languages %}
                            {% set langClass = attribute(hit._source, "show_#{locale}")|default(false) ? 'primary': 'default' %}
                            <span class="label {{ "label-#{langClass}" }}" data-toggle="tooltip" data-placement="top" aria-hidden="true" title="{{ attribute(hit._source, "title_#{locale}") }}">{{ locale|upper }}</span>
                        {% endfor %}
                    </td>
                    <td class="text-center" data-order="{{ environments|filter(v => hit._id in attribute(published, v)|keys)|join(' ') }}">
                        <div class="btn-group">
                            {% for env in environments %}
                                {% set isPublished = (hit._id in attribute(published, env)|keys ? true : false) %}
                                {% set isAligned = (hit._source._sha1 == attribute(attribute(published, env), hit._id)|default(false) ? true : false) %}
                                {% set envClass = (isAligned ? 'primary' : (isPublished ? 'default' : 'danger') ) %}
                                
                                {% if publishViewId and isPublished %}
                                    <a class="btn btn-xs {{ "btn-#{envClass}" }}" href="{{ path('ems_data_custom_template_protected', {
                                        'environmentName': env, 
                                        'templateId': publishViewId,
                                        'ouuid': hit._id
                                    }) }}">{{ env|capitalize }}</a>
                                {% else %}
                                    <button type="button" class="btn btn-xs {{ "btn-#{envClass}" }}" {{ envClass == "danger" ? 'disabled' }}>{{ env|capitalize }}</button>
                                {% endif %}
                            {% endfor %}
                        </div>
                    </td>
                    <td class="text-center" data-order="{{ hit._source._finalization_datetime }}">
                        <i class="fa fa-info-circle text-primary" data-toggle="tooltip" data-placement="top" data-html="true"  aria-hidden="true" title="{{ info }}"></i>
                    </td>
                    {% if is_granted('ROLE_COPY_PASTE') or is_granted('ROLE_ADMIN') %}
                        <td>
                            <div class="btn-group">
                                {% if is_granted('ROLE_COPY_PASTE') %}
                                    <a class="btn btn-xs btn-default" href="{{ path('revision.copy', {'environment': contentType.environment.name, 'type': contentType.name, 'ouuid': hit._id } ) }}">
            							<i class="fa fa-copy"></i>
            						</a>
        						{% endif %}
        						{% if is_granted('ROLE_ADMIN') %}
            						<a class="btn btn-xs btn-default" href="{{ path('data.view', {'environmentName': contentType.environment.name, 'type': contentType.name, 'ouuid':  hit._id} ) }}">
            							<i class="fa fa-file-text"></i>
            						</a>
        						{% endif %}
    						</div>
                        </td>
                    {% endif %}
                </tr>
            {% endfor %}
        </tbody>
    </table>
</div>
