properties.body:
{% set config = 'config'|i18n('en')|json_decode %}

{% set sourceTargets = source.target_group|default([]) %}
{% set targets = {
    size: 100,
    index: environment.alias,
    body: {
        query: {
            term: {
                _contenttype: 'target'
            }
        }
    }
}|search.hits.hits %}

{% set targetSlugs = {} %}
{% for hit in targets %}
    {% set byUuids = {} %}
    {% for uid in hit._source.menu_ids|default([]) %}
        {% set byUuids = byUuids|merge({(uid): { slug: hit._source.slugs[loop.index0]|default('') } }) %}
    {% endfor %}
    {% set targetSlugs = targetSlugs|merge({(hit._source.target_group ~ '_' ~ hit._source.locales): byUuids }) %}
{% endfor %}

<div class="table-responsive">
    <table class="table">
        <caption>List of targets</caption>
        <thead>
            <tr>
                <th scope="label">Languages</th>
                {% for label, target_group in config.target_groups %}
                    <th scope="label" class="text-center">{{ label }}</th>
                {% endfor %}
            </tr>
        </thead>
        {% for label, locale in config.languages %}
            <tr>
                <th scope="row">{{ label }}</th>
                {% for label, target_group in config.target_groups %}
                    {% set slugs = attribute(targetSlugs, target_group ~ '_' ~ locale)|default([])  %}
                    {% set baseUrl = [('baseUrl.'~environment.name)|i18n(locale), "targetGroup.#{target_group}"|i18n(locale)]|join('/')   %}
                    
                    {% if (target_group in sourceTargets or sourceTargets is empty) and  attribute(source, "show_#{locale}")|default(false) == true %}
                        {% set menus = attribute(source, "menu_#{locale}")|default([]) %}
                        
                        {% if menus %}
                            {% set menuItem = menus|filter(v => v in slugs|keys)|first %}
                            {% set slug = attribute(slugs, menuItem).slug|default(false) %}
                            
                            {% set url = slug ? (baseUrl ~ '/' ~ slug) : baseUrl %}
                        {% else %}
                            {% set url = [baseUrl, attribute(source, "slug_#{locale}")|default('')]|join('/') %}
                        {% endif %}
                        
                        <td class="text-center">
                            <a target="_blank" href="{{ url }}" class="btn btn-primary btn-sm">
                                <i class="fa fa-eye" aria-hidden="true"></i> 
                                show
                            </a>
                        </td>
                    {% else %}
                        <td class="text-center"><span><i class="fa fa-times text-danger" aria-hidden="true"></i></span></td>
                    {% endif %}
                {% endfor %}
            </tr>
        {% endfor %}
    </table>
</div

options.template:
{% set config = 'config'|i18n('en')|json_decode %}

{% set targets = {
    "index": environment.alias,
    "size": 100,
    "body": {
        "query": {
            "bool": {
                "must": [
                    {
                        "term": {
                        "_contenttype": {
                            "value": "target"
                        }
                    }
                    }
                ]
            }
        },
        "sort": {
            "target_group": {
                "order": "asc",
                "missing": "_last",
                "unmapped_type": "long"
            },
            "locales": {
                "order": "asc"
            }
        }
    }
}|search.hits.hits %}

{% set targetId = app.request.query.get('target', false) %}

{% if targetId == false %}
    <div class="table-responsive">
        <table class="table">
            <caption>Sitemaps</caption>
            <thead>
            <tr>
                {% for label, locale in config.languages %}
                    <th scope="label">{{ label }}</th>
                {% endfor %}
            </tr>
            </thead>
            <tbody>
            {% for label, target_group in config.target_groups %}
                <tr>
                    {% for label, locale in config.languages %}
                        {% set targetLocale = targets|filter(v => v._source.locales == locale and v._source.target_group == target_group)|first %}
                        <td>
                            {% if targetLocale %}
                                <a class="btn btn-sm btn-primary" href="?target={{ targetLocale._id  }}">
                                    <i class="fa fa-sitemap"></i>
                                    {{ targetLocale._source.title }}
                                </a>
                            {% endif %}
                        </td>
                    {% endfor %}
                </tr>
            {% endfor%}
            </tbody>
        </table>
    </div>
{% else %}
    {% set target = targets|first|default(false) %}
    {% for item in targets %}
        {% if item._id == targetId %}
            {% set target = item %}
        {% endif %}
    {% endfor %}

    {% set menuJson = target._source.menu|default('{}')|ems_json_menu_decode %}
    {% set documents = {
        "index": environment.alias,
        "size": 100,
        "body": {
            "query": {
                "bool": {
                    "must": [
                        {
                            "terms": {
                            "_contenttype": ['page', 'link']
                        }
                        },
                        {
                            "terms": {
                            ('menu_' ~ target._source.locales): menuJson.getUids()
                        }
                        }
                    ]
                }
            }
        }
    }|search.hits.hits %}

    {% set documentById = {} %}
    {% for item in documents %}
        {% for uid in attribute(item._source, 'menu_' ~ target._source.locales)|default([]) %}
            {% if uid in menuJson.getUids() %}
                {% set documentById = documentById|merge({(uid): item}) %}
            {% endif %}
        {% endfor %}
    {% endfor %}

    {% set items = {} %}

    {% block recursiveMenu %}
        {% set currentExpended = expanded|default('true') %}
        {% set expanded = 'false' %}
        {% for item in items|default([]) %}
            {% set contentType = item.contentType|get_content_type %}
            <li>
                <div class="box-header">
                    {% if item.children|default([])|length > 0 %}
                        <button href="#" type="button" title="{{ 'field_type.json_menu_editor.collapse_button'|trans }}" aria-expanded="{{ currentExpended }}" class="btn btn-default button-collapse"></button>
                        &nbsp;
                    {% endif %}

                    <a href="{{ ('baseUrl.'~environment.name)|i18n(target._source.locales) }}/{{ ('targetGroup.'~target._source.target_group)|i18n(target._source.locales) }}/{{ attribute(urlByUid, item.id)|default('')  }}" target="_blank">
                        <i class="{{ contentType.icon }}"></i>
                        <h3 class="box-title">{{ item.label }}</h3>
                    </a>
                    <i class="fa fa-exclamation-triangle text-red" style="display: none;"></i>
                    <div class="btn-group pull-right">

                        <div class="btn-group">
                            {% if attribute(documentById, item.id)|default(false) %}
                                <button type="button" class="btn btn-primary dropdown-toggle" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                    <i class="fa fa-check-square-o"></i>
                                    {{ attribute(attribute(documentById, item.id)._source, 'title_' ~ target._source.locales) }}
                                    <span class="caret"></span>
                                </button>
                                <ul class="dropdown-menu">
                                    <li>
                                        <a href="{{ path('data.revision_in_environment', {
                                            'environment': environment.name,
                                            'type': contentType.name,
                                            'ouuid': attribute(documentById, item.id)._id
                                        }) }}" class="">
                                            <i class="fa fa-list-ol"></i>
                                            Revision
                                        </a>
                                    </li>
                                    <li>
                                        <a href="{{ path('revision.new-draft', {
                                            'type': contentType.name,
                                            'ouuid': attribute(documentById, item.id)._id
                                        }) }}" class="">
                                            <i class="fa fa-edit"></i>
                                            Edit
                                        </a>
                                    </li>
                                </ul>
                            {% elseif '/' in attribute(urlByUid, item.id)|default('') %}
                                <button type="button" class="btn btn-danger dropdown-toggle" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                    <i class="fa fa-square-o"></i>
                                    Missing
                                    <span class="caret"></span>
                                </button>
                                <ul class="dropdown-menu">
                                    <li>
                                        {% include '@EMSCore/elements/post-button.html.twig' with {
                                            'url': path('emsco_data_add_from_jsoncontent', {'contentType': contentType.id}),
                                            'label': 'Create from scratch',
                                            'icon': 'plus',
                                            'btnClass': '',
                                            'value': {  ('menu_' ~target._source.locales): item.id, ('title_' ~target._source.locales): item.label, ('show_' ~target._source.locales): true  }|json_encode,
                                            'name': 'JSON_BODY' } %}
                                    </li>
                                </ul>
                            {% endif %}
                        </div>
                    </div>
                </div>
                {% if item.children|default([])|length > 0 %}
                    {% set items = item.children %}
                    <ol style="{% if currentExpended == 'false' %}display: none;{% endif %}">
                        {{ block('recursiveMenu') }}
                    </ol>
                {% endif %}
            </li>
        {% endfor %}
    {% endblock recursiveMenu %}
    {% set items = menuJson.structure %}

    {% set urlByUid = {} %}
    {% for uid in target._source.menu_ids|default([]) %}
        {% set urlByUid = urlByUid|merge({(uid): loop.first ? '' : target._source.slugs|default([])[loop.index0]|default('')}) %}
    {% endfor %}

    <div class="row">
        <div class="col-md-12" style="margin-bottom: 25px;">
            <a href="{{ path(app.request.get('_route'), app.request.get('_route_params')) }}" class="btn btn-primary">
                <i class="fa fa-chevron-left" aria-hidden="true"></i>
                &nbsp;
                back
            </a>
        </div>
        <div class="col-md-12">
            <div class="input-group">
    			<span class="input-group-btn">
    				<button type="#" title="{{ 'field_type.json_menu_editor.collapse_all_button'|trans }}" aria-expanded="false" class="btn btn-default button-collapse-all">
    				    &nbsp;Expand/Collapse all
    				</button>
    			</span>
            </div>
            <ol class="vertical">
                {{ block('recursiveMenu') }}
            </ol>
            <div class="btn-group">
                {% if is_granted('ROLE_WEBMASTER') %}
                    {% include '@EMSCore/elements/post-button.html.twig' with {
                        url:  path('revision.new-draft', {type: target._source._contenttype, ouuid: target._id}),
                        message: 'Edit target ' ~ target._source.label,
                        label: 'Edit target '~ target._source.label,
                        icon: 'edit',
                        class: 'btn btn-danger'
                    }%}
                {% endif %}
            </div>
        </div>
    </div>
{% endif %}

options.body:
{% spaceless %}
    {% set must = [] %}
    {% set body = {
        "sort": [
            {
              "_finalization_datetime": {
                "order": "desc"
              }
            }
        ] 
    } %}
    
    {% if app.request.query.get('q', false) %}
        {% set must = must|merge({ 
            "query_string": {
                "query": app.request.query.get('q')|default('*'),
                "default_field": app.request.query.get('fields')|default('_all')
            } 
        }) %}
    {% endif %}
    {% if app.request.query.get('search_type', false) %}
        {% set must = must|merge([{
            'term': {"search_type": app.request.query.get('search_type') }    
        }]) %}
    {% endif %}
    {% if app.request.query.get('search_category', false) %}
        {% set must = must|merge([{
            'term': {"search_category": app.request.query.get('search_category') }    
        }]) %}
    {% endif %}
    {% if app.request.query.get('target', false) %}
        {% set must = must|merge([{
            'term': {"target_group": app.request.query.get('target') }    
        }]) %}
    {% endif %}
    
    {% if must %}{% set body = body|merge({'query': { "bool": { "must": must } } } ) %}{% endif %}
{% endspaceless %}
{{- body|json_encode|raw -}}

options.template:
{% set tableLength = 100 %}
{% set environments = ['preview', 'live'] %}
{% set publishViewId = 59 %}
{% set config = 'config'|i18n('en')|json_decode %}

{% set published = [] %}
{% for env in environments %}
    {% set envHits = {
        "index": ("#{config.instance_id}#{env}"),
        "size": 3000,
        "_source": ['_sha1'],
        "body": {"query": {"term": { "_contenttype": { "value": "#{contentType.name}" } } } }
    }|search.hits.hits %}
    {% set envIdSha1 = [] %}
    {% for hit in envHits %}
        {% set envIdSha1 = envIdSha1|merge({ (hit._id): (hit._source._sha1) }) %}
    {% endfor %}
    {% set published = published|merge({ (env): (envIdSha1) }) %}
{% endfor %}

<form method="GET" class="form">
    <div class="well" style="padding: 15px;">
        <div class="row" style="padding-bottom: 10px;">
            <div class="col-md-4">
                <select name="target" class="form-control input-sm">
                    <option value="">All targets</option>
                    {% for tLabel, tValue in config.target_groups %}
                        <option value="{{ tValue }}" {% if app.request.query.get('target')|default(false) == tValue %}selected{% endif %}>Only {{ tLabel }}</option>
                    {% endfor %}
                </select>
            </div>
            <div class="col-md-4">
                <select name="search_type" class="form-control input-sm">
                    <option value="">All types</option>
                    {% set searchTypes = {
                        "index": ("#{config.instance_id}#{contentType.environment.name}"),
                        "body": { 
                            "size": 0,  
                            "query": { "term": {"_contenttype": ("#{contentType.name}")} },
                            "aggs": { "types": { "terms": { "field": "search_type", "size": 50 } } } 
                        }
                    }|search.aggregations.types.buckets|default([]) %}
                    {% for sT in searchTypes %}
                         <option value="{{ sT.key }}" {% if app.request.query.get('search_type')|default(false) == sT.key %}selected{% endif %}>{{ sT.key }}</option>
                    {% endfor %}
                </select>
            </div>
            <div class="col-md-4">
                <select name="search_category" class="form-control input-sm">
                    <option value="">All categories</option>
                    {% set searchCategories = {
                        "index": ("#{config.instance_id}#{contentType.environment.name}"),
                        "body": { 
                            "size": 0,  
                            "query": { "term": {"_contenttype": ("#{contentType.name}")} },
                            "aggs": { "types": { "terms": { "field": "search_category", "size": 50 } } } 
                        }
                    }|search.aggregations.types.buckets|default([]) %}
                    {% for sC in searchCategories %}
                         <option value="{{ sC.key }}" {% if app.request.query.get('search_category')|default(false) == sC.key %}selected{% endif %}>{{ sC.key|data.title_en|default(sC.key) }}</option>
                    {% endfor %}
                </select>
            </div>
        </div>
        <div class="row">
            <div class="col-md-4">
                <select class="form-control input-sm" name="fields">
                    <option value="_all" {% if app.request.query.get('fields')|default(false) == "_all" %}selected{% endif %}>All fields</option>
                    {% for label, locale in config.languages %}
                        <option value="{{ "all_#{locale}" }}" {% if app.request.query.get('fields')|default(false) == "all_#{locale}" %}selected{% endif %}>{{ "Fields in #{label}" }}</option>
                    {% endfor %}
                </select>
            </div>
            <div class="col-md-4">
                <input type="text" class="form-control input-sm" placeholder="Search text (abc*)" name="q" value="{{ app.request.query.get('q')|default('') }}">
            </div>
            <div class="col-md-4">
                <div>
                    <div class="btn-group">
                        <button type="submit" class="btn btn-sm btn-primary">Search</button>
                        <a href="{{ path('data.customindexview', { viewId: view.id}) }}" class="btn  btn-sm btn-primary">Reset</a>
                    </div>
                    {% if is_granted(contentType.createRole) %}
                        <a href="{{ path('data.add', {contentType: contentType.id}) }}" class="btn btn-sm btn-success pull-right"><i class="fa fa-plus"></i>&nbsp;Add </a>        
                    {% endif%}
                </div>
            </div>
       </div>
    </div>
</form>

<div class="table-responsive">
    <table id="overview" class="table table-bordered table-striped" data-page-length="{{ tableLength }}" >
        <thead>
            <tr>
                {% if is_granted(contentType.editRole) %}<th style="width: 20px;" data-orderable="false"></th>{% endif %}
                <th>label</th>
                <th class="text-center" style="width: 100px;">languages</th>
                <th class="text-center" style="width: 100px;">published</th>
                <th class="text-center" style="width: 20px;">info</th>
                {% if is_granted('ROLE_COPY_PASTE') or is_granted('ROLE_ADMIN') %}<th data-orderable="false" style="width: 60px;"></th>{% endif %}
            </tr>
        </thead>
        <tbody>
            {% for hit in result.hits.hits %}
                {% set info %}
                   {{ hit._source._finalization_datetime|date('d-m-Y H:i') }}
                   <br />
                   {{ hit._source._finalized_by is defined ? hit._source._finalized_by : '' }}
                {% endset %}
                <tr>
                    {% if is_granted(contentType.editRole) %}
                        <td><a class="btn btn-xs btn-primary" href="{{ path('revision.new-draft', {'type': contentType.name, 'ouuid': hit._id }) }}"><i class="fa fa-pencil" /></a></td>
                    {% endif %}
                    <td><a href="{{ path('ems_content_revisions_view', {'type': contentType.name, 'ouuid': hit._id }) }}" >{{ hit._source.label }}</a></td>
                    <td class="text-center" data-order="{{ config.languages|filter(v => attribute(hit._source, "show_#{v}") == true)|join(' ') }}">
                        {% for lang, locale in config.languages %}
                            {% set langClass = attribute(hit._source, "show_#{locale}")|default(false) ? 'primary': 'default' %}
                            <span class="label {{ "label-#{langClass}" }}" data-toggle="tooltip" data-placement="top" aria-hidden="true" title="{{ attribute(hit._source, "title_#{locale}") }}">{{ locale|upper }}</span>
                        {% endfor %}
                    </td>
                    <td class="text-center" data-order="{{ environments|filter(v => hit._id in attribute(published, v)|keys)|join(' ') }}">
                        <div class="btn-group">
                            {% for env in environments %}
                                {% set isPublished = (hit._id in attribute(published, env)|keys ? true : false) %}
                                {% set isAligned = (hit._source._sha1 == attribute(attribute(published, env), hit._id)|default(false) ? true : false) %}
                                {% set envClass = (isAligned ? 'primary' : (isPublished ? 'default' : 'danger') ) %}
                                
                                {% if publishViewId and isPublished %}
                                    <a class="btn btn-xs {{ "btn-#{envClass}" }}" href="{{ path('ems_data_custom_template_protected', {
                                        'environmentName': env, 
                                        'templateId': publishViewId,
                                        'ouuid': hit._id
                                    }) }}">{{ env|capitalize }}</a>
                                {% else %}
                                    <button type="button" class="btn btn-xs {{ "btn-#{envClass}" }}" {{ envClass == "danger" ? 'disabled' }}>{{ env|capitalize }}</button>
                                {% endif %}
                            {% endfor %}
                        </div>
                    </td>
                    <td class="text-center" data-order="{{ hit._source._finalization_datetime }}">
                        <i class="fa fa-info-circle text-primary" data-toggle="tooltip" data-placement="top" data-html="true"  aria-hidden="true" title="{{ info }}"></i>
                    </td>
                    {% if is_granted('ROLE_COPY_PASTE') or is_granted('ROLE_ADMIN') %}
                        <td>
                            <div class="btn-group">
                                {% if is_granted('ROLE_COPY_PASTE') %}
                                    <a class="btn btn-xs btn-default" href="{{ path('revision.copy', {'environment': contentType.environment.name, 'type': contentType.name, 'ouuid': hit._id } ) }}">
            							<i class="fa fa-copy"></i>
            						</a>
        						{% endif %}
        						{% if is_granted('ROLE_ADMIN') %}
            						<a class="btn btn-xs btn-default" href="{{ path('data.view', {'environmentName': contentType.environment.name, 'type': contentType.name, 'ouuid':  hit._id} ) }}">
            							<i class="fa fa-file-text"></i>
            						</a>
        						{% endif %}
    						</div>
                        </td>
                    {% endif %}
                </tr>
            {% endfor %}
        </tbody>
    </table>
</div>
